actions.script:
import co.hotwax.util.CsvWriteHelper

 try (CsvWriteHelper csvWriteHelper = new CsvWriteHelper(baseFilePath, null, recordLimitPerFeed) ) {
    for (row in reportRows) {
        System.out.println("Writing reconciliation report row: " + row)
        csvWriteHelper.writeToFile(row)
    }
    generatedFilePaths = csvWriteHelper.getGeneratedFilePaths()
 } catch (Exception e) {
    throw new RuntimeException("Error generating order reconciliation report CSV at ${baseFilePath}: " + e.toString(), e)
 }
