actions.script:
queryString = new StringBuffer()
queryString.append("status=").append(status)
if (createdFromDate) queryString.append("&&created_at_min=").append(ZonedDateTime.ofInstant(Timestamp.valueOf(createdFromDate).toInstant(), ZoneId.of("UTC")).toString())
if (createdThruDate) queryString.append("&&created_at_max=").append(ZonedDateTime.ofInstant(Timestamp.valueOf(createdThruDate).toInstant(), ZoneId.of("UTC")).toString())
if (updatedFromDate) queryString.append("&&updated_at_min=").append(ZonedDateTime.ofInstant(Timestamp.valueOf(updatedFromDate).toInstant(), ZoneId.of("UTC")).toString())
if (updatedThruDate) queryString.append("&&updated_at_max=").append(ZonedDateTime.ofInstant(Timestamp.valueOf(updatedThruDate).toInstant(), ZoneId.of("UTC")).toString())
if (financialStatus) queryString.append("&&financial_status=").append(financialStatus)
if (fulfilmentStatus) queryString.append("&&fulfillment_status=").append(fulfilmentStatus)
