while.script:
StringWriter sw = new StringWriter()
ec.resource.ftlTemplateRenderer.render(systemMessage.sendPath, sw)
queryText = sw.toString()
ec.logger.info ("queryText" + queryText)
try {
    sw.close()
} catch (IOException e) {
    ec.logger.error("Error closing StringWriter")
}

